import React, { useState } from 'react';
import { Query } from 'react-apollo';
import { gql } from 'apollo-boost';

import './profile.css';

const Profile = () => {
   const [hikerID, setHikerID] = useState(null);

   return (
      <div>
         {/* <Query query={IS_LOGGED_IN_QUERY}>
            {({data}) => data.isLoggedIn ? <div>You're logged in</div> : <div>Not logged in</div>}
         </Query> */}
         {!hikerID
         ?  <Query query={ME_QUERY}>
               {({data, loading, error}) => {
                  if (error) {
                     return <div>Not Logged In</div>
                  }

                  console.log(data);
                  // setHikerID(3)
                  return (
                     <div>TEMPORARY DIV !!!!!</div>
                  )
               }}
            </Query>
         :  <Query query={HIKER_QUERY} variables={{hikerID:hikerID}}>
               {({data, loading, error}) => {

                  // console.log(data)
                  return (
                     <div>Logged In. ID:</div>
                  )
               }}
            </Query>
         }


      </div>

   )
}

const IS_LOGGED_IN_QUERY = gql`
   {
      isLoggedIn @client
   }
`

const ME_QUERY = gql`
   {
      me {
         id
      }
   }
`

const HIKER_QUERY = gql`
   query ($hikerID: Int!) {
      hiker(hikerID: $hikerID) {
         id 
         user {
            username
         }
      }
   }
`

export default Profile;
