import React, { useState } from 'react';
import { Query } from 'react-apollo';
import { gql } from 'apollo-boost';

import './profile.css';

const Profile = () => {
   const [hikerID, setHikerID] = useState(null);

   return (
      <div>
         {/* <Query query={IS_LOGGED_IN_QUERY}>
            {({data}) => data.isLoggedIn ? <div>You're logged in</div> : <div>Not logged in</div>}
         </Query> */}

         {/* {!hikerID
         ?  <Query query={ME_QUERY}>
               {({data, loading, error}) => {
                  if (error) return <div>Not Logged In</div>
                  if (loading) return <div></div>

                  console.log(data);
                  setHikerID(data.me.id)

                  return <div></div>
               }}
            </Query>
         :  <Query query={HIKER_QUERY} variables={{hikerID:hikerID}}>
               {({data, loading, error}) => {
                  if (error) return <div>Error</div>
                  if (loading) return <div></div>

                  console.log(data)

                  return (
                     <div>Logged In. ID: {data.hiker.id} username: {data.hiker.user.username}</div>
                  )
               }}
            </Query>
         } */}

         <Query query={HIKER_QUERY}>
            {({data, loading, error}) => {
               if (error) return <div>Error</div>
               if (loading) return <div></div>

               console.log(data)

               return (
                  <div>Logged In. ID: {data.hiker.id} username: {data.hiker.user.username}</div>
               )
            }}
         </Query>

      </div>

   )
}

const IS_LOGGED_IN_QUERY = gql`
   {
      isLoggedIn @client
   }
`

const ME_QUERY = gql`
   {
      me {
         id
      }
   }
`

// const HIKER_QUERY = gql`
//    query ($hikerID: Int!) {
//       hiker(hikerID: $hikerID) {
//          id 
//          user {
//             username
//          }
//       }
//    }
// `

const HIKER_QUERY = gql`
   query {
      hiker {
         id 
         user {
            username
         }
      }
   }
`

export default Profile;
