import React, { Component, useState } from 'react';
import { ApolloConsumer } from 'react-apollo';
import { gql } from 'apollo-boost';

import './searchTrails.css';

const SearchTrails = (props) => {
   const [search, setSearch] = useState("")

   const handleSubmit = async (event, client) => {
      event.preventDefault();
      const res = await client.query({
         query: SEARCH_TRAILS_QUERY,
         variables: { search }
      })
      props.setSearchResults(res.data.trails)
   }

   return (
      <ApolloConsumer>
         {client => (
            <div className="searchBarContainer">
               <div className="searchSpace"></div>
               <form className="searchBar" onSubmit={event => handleSubmit(event, client)}>
                  <button type="submit" className="clear">X</button>
                  <input type="text" className="searchField" onChange={event => setSearch(event.target.value)} />
                  <button type="submit" className="submitSearch"><img src="http://localhost:8000/uhg/static/searchIcon.png" alt=""/></button>
               </form>
            </div>
         )}

      </ApolloConsumer>
   )
}

const SEARCH_TRAILS_QUERY = gql`
   query($search: String) {
      trails(search: $search) {
         name 
         prop 
         city 
         state
      }
   }
`

export default SearchTrails;
