import React, { useState } from 'react';
import { Query } from 'react-apollo';
import { gql } from 'apollo-boost';

import Header from '../../components/header/header';
import SearchTrails from '../../components/searchTrails/searchTrails';

import './traildetail.css';

const TrailDetail = (props) => {
   const [searchResults, setSearchResults] = useState([])
   const [resultIdx, setResultIdx] = useState(0);


   return (
      <div className="trailDetailPg">
         <Header currentPg="home" />
         <Query query={TRAIL_DETAIL_QUERY} variables={{trailID:props.match.params.id}}>
            {({data, loading, error}) => {
               if (loading) return <div></div>
               if (error) return <div>Error</div>

               console.log(data)

               return (
                  <div>
                     <div className="topDetailsContainer">
                        <div className="namePropBox">
                           <div className="nmPropLn1">{data.trailDetails.name}</div>
                           <div className="nmPropLn2">{data.trailDetails.prop}</div>
                        </div>
                        <div className="openFeeBox">
                           <div className="openFeeItem">{data.trailDetails.isOpen ? 'OPEN' : 'CLOSED'}</div>
                           <div className="openFeeItem">{data.trailDetails.fee===0 ? 'FREE' : `$${data.trailDetails.fee.toFixed(2)}`}</div>
                        </div>
                        <div className="topDetailsSpace"></div>
                     </div>
                  </div>
               )
            }}
         </Query>
         <SearchTrails setSearchResults={setSearchResults} setResultIdx={setResultIdx} />


      </div>





   )
}

const TRAIL_DETAIL_QUERY = gql`
   query ($trailID: Int!) {
      trailDetails(trailID: $trailID) {
         name 
         prop 
         city
         state
         description
         isOpen
         altitudeChange
         distance
         fee
         image
         suggestedEquipment {
            equipmentTypeID {
               equType
            }
         }
         tags {
            tag
         }
         avgDifficulty 
         avgEnjoyability
         image
      }
   }
`

export default TrailDetail;
