import React, { Component } from 'react';
import { Query } from 'react-apollo';
import { gql } from 'apollo-boost';

import TrailItem from '../trailItem/trailItem';

import './trailsview.css';

class TrailsView extends Component {
   state = {
      startIdx: 0
   }

   render() {
      let start = this.startIdx;
      return (
         <Query query={BEGINNER_TRAILS}>
            {({data, loading, error}) => {
               if (loading) return <div>Loading</div>
               if (error) return <div>Error</div>

               let trailsList = data.beginnerTrails.map((trail, idx) => {
                  return <TrailItem
                     key={idx}
                     trailID={trail.id}
                     trailName={trail.name}
                     trailProp={trail.prop}
                     trailDistance={trail.distance}
                     trailAltChange={trail.altitudeChange}
                     trailAvgDiff={trail.avgDifficulty}
                     trailAvgEnj={trail.avgEnjoyability}
                  />
               })

               return (
                  <div className="trailsView">
                     <div className="outerSection">&lt;</div>
                     {trailsList.slice(start, start+5)}
                     <div className="outerSection grayedOut">&gt;</div>
                  </div>
               )
            }}
         </Query>
      )
   }
}


const BEGINNER_TRAILS = gql`
   {
      beginnerTrails {
         id 
         name 
         prop 
         distance 
         altitudeChange
         avgDifficulty
         avgEnjoyability
      }
   }
`

export default TrailsView;
